
const JSmodal={closer:0,init:function(){document.getElementsByTagName("body")[0].innerHTML+=`
      <div id="JSmodal" class="JSmodal">
        <div class="JSmodal-content">
          <span class="JSclose" onclick="JSmodal.close();">&times;</span>
          <p></p>
        </div>
      </div>
    `;window.onclick=function(event){if(event.target==document.getElementById('JSmodal')){if(JSmodal.closer===1){document.getElementById('JSmodal').style.display="none";}}};},open:function(mode,msg){if(arguments.length<2){alert("Missing mode arg in JSmodal.open(mode, msg)");return;}
JSmodal.closer=mode;document.querySelector(".JSmodal-content p").innerHTML=msg;document.getElementById('JSmodal').style.display="block";},close:function(){document.getElementById('JSmodal').style.display="none";},confirm:function(routine,msg){msg+=`
      <br><br>
      <button onclick="${routine}">&nbsp;&nbsp;&nbsp;Confirm&nbsp;&nbsp;&nbsp;</button>
    `;JSmodal.open(0,msg);},prompt:function(routine,msg){msg+=`
      <br><br>
      <input type="text" id="JSid" size="32"><br><br>
      <button onclick="${routine}">&nbsp;&nbsp;&nbsp; OK &nbsp;&nbsp;&nbsp;</button>
    `;JSmodal.open(0,msg);}};document.addEventListener("DOMContentLoaded",function(event){JSmodal.init();});